import React, { Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton } from 'react-mdl';

export default class Projects extends Component {
    constructor(props) {
        super(props)
        this.state = { activeTab: 0 };
    }

    toggleCategories(){
        if(this.state.activeTab === 0){
            return(
                <Card shadow={5} style={{width: '512px', margin: 'auto'}}>
                    <CardTitle style={{color: '#fff', height: '176px', background: 'url(https://www.google.com/imgres?imgurl=https%3A%2F%2Fupload.wikimedia.org%2Fwikipedia%2Fcommons%2Fthumb%2Fa%2Fa7%2FReact-icon.svg%2F1200px-React-icon.svg.png&imgrefurl=https%3A%2F%2Fen.wikipedia.org%2Fwiki%2FReact_(web_framework)&tbnid=bs9SxMY2Dnr1RM&vet=12ahUKEwjvo-uYo8vtAhUVzioKHTEFBO8QMygAegUIARCmAQ..i&docid=HUX3vxMryA4w3M&w=1200&h=848&q=react%20image&client=firefox-b-d&ved=2ahUKEwjvo-uYo8vtAhUVzioKHTEFBO8QMygAegUIARCmAQ) center / cover'}}>React Project #1</CardTitle>
                    <CardText>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                        Mauris sagittis pellentesque lacus eleifend lacinia...
                    </CardText>
                    <CardActions border>
                        <Button colored>Get Started</Button>
                    </CardActions>
                    <CardMenu style={{color: '#fff'}}>
                        <IconButton name="share" />
                    </CardMenu>
                </Card>
            );
        }else if(this.state.activeTab === 1){
            return(
                <div>
                    <h1>This is Java</h1>
                </div>
            );
        }else if(this.state.activeTab === 2){
            return(
                <div>
                    <h1>This is HTML & CSS</h1>
                </div>
            );
        }
    }

    render() {
        return (
            <div className="category-tab">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                    <Tab>React</Tab>
                    <Tab>Java</Tab>
                    <Tab>HTML & CSS</Tab>
                </Tabs>
                <section>
                    <Grid className='projects-grid'>
                        <Cell col={12}>
                            <div className="projects-grid">{this.toggleCategories()}</div>
                        </Cell>
                    </Grid>
                </section>
            </div>
        )
    }
}
