import React, { Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton } from 'react-mdl';

export default class Projects extends Component {
    constructor(props) {
        super(props)
        this.state = { activeTab: 0 };
    }

    function(){
        <a href="https://github.com/manjuarya"></a>
    }

    toggleCategories(){
        if(this.state.activeTab === 0){
            return(
                <div style={{display: 'flex'}}>
                    {/*PROJECT 1*/}
                    <Card shadow={5} style={{width: '400px', margin: 'auto'}}>
                        <CardTitle style={{color: '#fff', height: '200px', background: 'url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>React Project #1</CardTitle>
                        <CardText style={{fontSize: '15px'}}>
                            Video Streaming App 
                        </CardText> 
                        <CardActions border>
                            <a href="https://github.com/manjuarya/VideoStreamingApp" rel="noopener noreferrer" target="_blank">
                                <Button colored>GitHub</Button>
                            </a>
                            <a href="https://videostreamingapp.netlify.app" rel="noopener noreferrer" target="_blank">
                                <Button colored>Netlify</Button>
                            </a>
                        </CardActions>
                        <CardMenu style={{color: '#fff'}}>
                            <IconButton name="share" />
                        </CardMenu>
                    </Card>

                    {/*PROJECT 2*/}
                    <Card shadow={5} style={{width: '400px', margin: 'auto'}}>
                        <CardTitle style={{color: '#fff', height: '200px', background: 'url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>React Project #2</CardTitle>
                        <CardText>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Mauris sagittis pellentesque lacus eleifend lacinia...
                        </CardText>
                        <CardActions border>
                            <a href="https://github.com/manjuarya" rel="noopener noreferrer" target="_blank">
                                <Button colored>GitHub</Button>
                            </a>
                            <a href="https://github.com/manjuarya" rel="noopener noreferrer" target="_blank">
                                <Button colored>Netlify</Button>
                            </a>
                        </CardActions>
                        <CardMenu style={{color: '#fff'}}>
                            <IconButton name="share" />
                        </CardMenu>
                    </Card>

                    {/*PROJECT 3*/}
                    <Card shadow={5} style={{width: '400px', margin: 'auto'}}>
                        <CardTitle style={{color: '#fff', height: '200px', background: 'url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>React Project #3</CardTitle>
                        <CardText>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Mauris sagittis pellentesque lacus eleifend lacinia...
                        </CardText>
                        <CardActions border>
                            <a href="https://github.com/manjuarya" rel="noopener noreferrer" target="_blank">
                                <Button colored>GitHub</Button>
                            </a>
                            <a href="https://github.com/manjuarya" rel="noopener noreferrer" target="_blank">
                                <Button colored>Netlify</Button>
                            </a>
                        </CardActions>
                    </Card>
                </div>
            );
        }else if(this.state.activeTab === 1){
            return(
                <div>
                    <h1>This is Java</h1>
                </div>
            );
        }else if(this.state.activeTab === 2){
            return(
                <div>
                    <h1>This is HTML & CSS</h1>
                </div>
            );
        }
    }

    render() {
        return (
            <div className="category-tab">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                    <Tab>React</Tab>
                    <Tab>Java</Tab>
                    <Tab>HTML & CSS</Tab>
                </Tabs>
                <section>
                    <Grid>
                        <Cell col={12}>
                            <div>{this.toggleCategories()}</div>
                        </Cell>
                    </Grid>
                </section>
            </div>
        )
    }
}
